import React, {Component, Fragment} from "react";
import {Helmet} from 'react-helmet';


class Quiz extends React.Component{
    // constructor(props){
    //     super(props);

    // }

    increamentCounter = ()=>{
        this.setState((prevState, props) => ({
            counter: prevState.counter + 1
        })); 
    }

    render(){
        return(
            <Fragment>
                <Helmet><title>Quiz Page</title></Helmet>
                <div className="questions">
                    <div className="lifeline-container">
                        <p>
                            <span className="mdi mdi-set-center mdi-24px lifeline-icon"></span>2
                        </p>
                        <p>
                            <span className="mdi mdi-lightbulb-on-outline mdi-24px lifeline-icon"></span>5
                        </p>
                    </div>
                    <div>
                        
                    </div>
                    <h5>Google was founded in what year?</h5>
                    <div className="options-container">
                        <p className="options">1997</p>
                        <p className="options">1998</p>
                    </div>
                    <div className="options-container">
                        <p className="options">1999</p>
                        <p className="options">2000</p>
                    </div>

                    <div className="button-container">
                        <button>Previous</button>
                        <button>Next</button>
                        <button>Quit</button>
                    </div>
                </div>
            </Fragment>
        );
    }

}

export default Quiz
