import React, {Component} from "react";


class Quiz extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            counter: 0
        };
    }

    increamentCounter = ()=>{
        this.setState({
            counter: 5
        })
    }

    render(){
        return(
            <div>
                <p>Counter: {this.state.counter}</p>
            <button onClick={this.increamentCounter}>Click me</button>
            </div>
            
        );
    }

}

export default Quiz
